function formatMongoDate(d) {
    var v = [];
    v[0] = d.substr(0, 4);
    v[1] = getExtMonth(d.substr(5, 2));
    v[2] = d.substr(8, 2);
    v[3] = d.substr(11, 2);
    v[4] = d.substr(14, 2);

    return "" + v[2] + " " + v[1] + " " + v[0] + "   -   " + v[3] + ":" + v[4];
}
function getExtMonth(m) {
    switch (m) {
        case "01":
            return "Jan";
            break;
        case "02":
            return "Fev";
            break;
        case "03":
            return "Mar";
            break;
        case "04":
            return "Abr";
            break;
        case "05":
            return "Mai";
            break;
        case "06":
            return "Jun";
            break;
        case "07":
            return "Jul";
            break;
        case "08":
            return "Ago";
            break;
        case "09":
            return "Set";
            break;
        case "10":
            return "Out";
            break;
        case "11":
            return "Nov";
            break;
        case "12":
            return "Dez";
            break;
    }
